    //       message: "Sent cart successfully!",
    //     })
    //   );
    // };
    // if (inInitial) {
    //   inInitial = false;
    //   return;
    // }
    // sendCartData().catch((error) => {
    // dispatch(
    //   uiActions.showNotification({
    //     status: "error",
    //     title: "error!",
    //     message: "Sending cart data failed!",
    //   })
    // );
    // });
  }, [cart, dispatch]);
  return (
    <Fragment>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
